sys_script_include.script:
var ImportUtil = Class.create();
ImportUtil.prototype = {
    initialize: function() {
    },

	importToTable: function(table, data, dataMap, uniqueColumn){
		if(uniqueColumn === undefined)
			uniqueColumn = "id";

		for(var i=0; i < data.length; i++){
			var rec = data[i];
			var gr = new GlideRecord(table);
			gr.get(dataMap[uniqueColumn], rec[uniqueColumn])
			if(!gr.isValidRecord())
				gr.initialize();

			for(var prop in dataMap)
				gr.setValue(dataMap[prop], rec[prop]);

			gr.update();
		}
	},

    type: 'ImportUtil'
};
