sys_script_include.script:
var JobImport = Class.create();
JobImport.prototype = {

	_endpoint: null,
	_server: null,
    initialize: function(server, integration) {
		this._server = server;
		if(integration == undefined || integration == null)
			integration = AnsibleIntegration.get();

		this._getEndpoint(integration);
    },

	run: function(){
		this._importProjects();
	},

	_getEndpoint: function(integration){
		var ep = integration.getEndpoint(AnsibleConstants.ENDPOINT_HOST);
		if(ep == null)
			throw '';

		this._endpoint = ep;
	},

	_importProjects: function(){
		var mess = new AMessage(this._server);	
		mess.setResource(this._endpoint.uri);
		var result = mess.execute();
		if(result.success){
			gs.debug("Import Success.");

			gs.debug(JSON.stringify(result.data));

		}else
			throw 'Failed to import projects.';
	},



    type: 'JobImport'
};
