sys_script_include.script:
var TemplateImport = Class.create();
TemplateImport.prototype = {
		_dataMap: {id: "id", name: "name", description: "description", inventory: "inventory", enabled: "enabled", variables: "variables"},
	_endpoint: null,
	_server: null,
    initialize: function(server, integration) {
		this._server = server;
		if(integration == undefined || integration == null)
			integration = AnsibleIntegration.get();

		this._getEndpoint(integration);
    },

	run: function(){
		this._importProjects();
	},

	_getEndpoint: function(integration){
		var ep = integration.getEndpoint(AnsibleConstants.ENDPOINT_TEMPLATE);
		if(ep == null)
			throw '';

		this._endpoint = ep;
	},

	_importProjects: function(){
		var mess = new AMessage(this._server);	
		mess.setResource(this._endpoint.uri);
		var result = mess.execute();
		if(result.success){
				gs.debug("Import Success.");

			new ImportUtil().importToTable(AnsibleConstants.TEMPLATE_TABLE,result.data.results, this._dataMap);
		}else
			throw 'Failed to import projects.';
	},



    type: 'TemplateImport'
};
