sys_script_include.script:
var ProjectImport = Class.create();
ProjectImport.prototype = {
	_dataMap: {id: "id", name: "name", description: "description"},
	_endpoint: null,
	_server: null,
    initialize: function(server, integration) {
		this._server = server;
		if(integration == undefined || integration == null)
			integration = AnsibleIntegration.get();

		this._getEndpoint(integration);
    },

	run: function(){
		this._importProjects();
	},

	_getEndpoint: function(integration){
		var ep = integration.getEndpoint(AnsibleConstants.ENDPOINT_PROJECT);
		if(ep == null)
			throw '';

		this._endpoint = ep;
	},

	_importProjects: function(){
		var mess = new AMessage(this._server);	
		mess.setResource(this._endpoint.uri);
		var result = mess.execute();
		if(result.success){
			gs.debug("Import Success.");

			new ImportUtil().importToTable(AnsibleConstants.PROJECT_TABLE,result.data.results, this._dataMap)
		}else
			throw 'Failed to import projects.';
	},


    type: 'ProjectImport'
};
